import { createFeatureSelector, createSelector } from "@ngrx/store";
import { accountAdapter } from './state';
const getAccountFeatureState = createFeatureSelector("accounts");
export const getAccounts = createSelector(getAccountFeatureState, accountAdapter.getSelectors().selectAll);
export const getAccountsLoading = createSelector(getAccountFeatureState, state => state.loading);
export const getAccountsLoaded = createSelector(getAccountFeatureState, state => state.loaded);
export const getError = createSelector(getAccountFeatureState, state => state.error);
export const getCurrentAccountId = createSelector(getAccountFeatureState, state => state.selectedAccountId);
export const getCurrentAccount = createSelector(getAccountFeatureState, getCurrentAccountId, state => state.entities[state.selectedAccountId]);
